import React, {Component} from 'react';
// import './App.css';
import Modal from './components/Modal';
import axios from 'axios';

class App extends Component{
  constructor(props){
    super(props);
    this.state={
      viewCompleted:false,
      activeItem: {
        title:"",
        description:"",
        completed:false,
      },
      taskList: [],
    };
  }

  componentDidMount(){
    this.refreshList();
  }

  refreshList = () => {
    axios
    .get("http://localhost:8000/api/tasks/")
    .then(res => this.state({taskList: res.data}))
    .catch(err => console.log(err))
  }

  displayCompleted = status => {
    if(status){
      return this.setState({viewCompleted:true});
    }
    return this.setState({viewCompleted:false});
  }

  renderTabList = () => {
    return(    
      <div className="my-5 tab-list">
        <span
          onClick={() => this.displayCompleted(true)}
          className={this.state.viewCompleted ? "active" : ""}>
            Completed
        </span>
        <span
          onClick={() => this.displayCompleted(false)}
          className={this.state.viewCompleted ? "" : "active"}>
            Incompleted
        </span>
      </div>
    )
  }

  renderItems = () => {
    const { viewCompleted } = this.state;
    const newItems = this.state.taskList.filter(
      item => item.completed === viewCompleted
    );
    return newItems.map(item => (
      <li
        key={item.id}
        className="list-group-item d-flex justify-content-between align-items-center"
      >
        <span
          className={`todo-title mr-2 ${this.state.viewCompleted ? "completed-todo" : ""
            }`}
          title={item.description}
        >
          {item.title}
        </span>
        <span>
          <button
            onClick={() => this.editItem(item)}
            className="btn btn-secondary mr-2"
          >
            Edit
          </button>
          <button
            onClick={() => this.handleDelete(item)}
            className="btn btn-danger"
          >
            Delete
          </button>
        </span>
      </li>
    ));
  };

  toggle = () => {
    this.setState({modal: !this.state.modal});
  };
  handleSubmit = item => {
    this.toggle();
    // alert('Saved!' + JSON.stringify(item));
    if(item.id){
      axios
        .put(`http://localhost:8000/api/tasks/${item.id}/`, item)
        .then(res => this.refreshList())
    }
    axios
      .post("http://localhost:8000/api/tasks/", item)
      .then(res => this.refreshList())
  };

  handleDelete = item => {
    // alert('Deleted!' + JSON.stringify(item));
    axios
        .delete(`http://localhost:8000/api/tasks/${item.id}/`)
        .then(res => this.refreshList())
  }
  createItem = () => {
    const item = {title: "", modal: !this.state.modal};
    this.setState({activeItem:item, modal: !this.state.modal});
  }
  editItem = item => {
    this.setState({activeItem: item, modal: !this.state.modal});
  }


  render(){
    return(
      <main className="content p-3 mb-2 bg-info">
        <h1 className="text-white text-uppercase text-center my-4">Task Manager</h1>
        <div className="row">
          <div className="col-md-6 col-sma-10 mx-auto p-0">
            <div className="card p-3">
              <div className="">
                <button onClick={this.createItem} className="btn btn-primary">Add Task</button>
              </div>
              {this.renderTabList()}
              <ul className="list-group list-group-flush">
              {this.renderItems()}
              </ul>
            </div>
          </div>
        </div>
        <footer className="my-3 mb-2 bg-info text-white text-center">Copyright 2023 &copy; All Rights Reserved</footer>
        {this.state.modal ? (
          <Modal activeItem={this.state.activeItem} toggle={this.toggle} onSave={this.handleSubmit}/>
        ) : null} 
      </main>
    )
  }
}


export default App;
