import React, {Component} from 'react';
import './App.css';
import CustomModal from './components/Modal'
const tasks = [
  {
    id: 1,
    title: "Call clients",
    description:":)=====D",
    completed:true
  },
  {
    id: 2,
    title: "dunning",
    description:"Some thing x 2",
    completed:false
  },
  {
    id: 3,
    title: "Order Release",
    description:"Check out customers account and release or vlock ordes acordingly.",
    completed:true
  },
  {
    id: 4,
    title: "Weekly Reporst",
    description:"Sending the weekly reports for overdiw invoices",
    completed:false   
  },
]

class App extends Component{
  constructor(props){
    super(props);
    this.state={
      modal:false,
      viewCompleted:false,
      activeItem: {
        title:"",
        description:"",
        completed:false,
      },
      taskList: tasks,
    };
  }

  toggle = () => {
    this.setState({modal: !this.state.modal});
  };
  handleSubmit = item => {
    this.toggle();
    alert('Saved!' + JSON.stringify(item));
  }
  handleDelete = item => {
    alert('Deleted!' + JSON.stringify(item));
  }
  createItem = () => {
    const item = {title: "", modal: !this.state.modal};
    this.setState({activeItem:item, modal: !this.state.modal});
  }
  editItem = item => {
    this.setState({activeItem: item, modal: !this.state.modal});
  }



  displayCompleted = status => {
    if(status){
      return this.setState({viewCompleted:true});
    }
    return this.setState({viewCompleted:false});
  }

  renderTabList = () => {
    return(    
      <div className="my-5 tab-list">
        <span
          onClick={() => this.displayCompleted(true)}
          className={this.state.viewCompleted ? "active" : ""}>
            Completed
        </span>
        <span
          onClick={() => this.displayCompleted(false)}
          className={this.state.viewCompleted ? "" : "active"}>
            Incompleted
        </span>
      </div>
    )
  }

  renderItems = () => {
    const{ viewCompleted } = this.state;
    const newItems = this.state.taskList.filter(
      item => item.completed == viewCompleted
    );
  return newItems.map(item => (
    <li 
    key={item.id}
    className="list-group-item d-flex justify-content-between align-item-center">
      <span className={`todo-title mr-2 ${this.state.viewCompleted ? "completed-todo" : "" }`} title={item.title}>
        {item.title}
      </span>
      <span>
        <button className="btn btn-info mr-2"> Edit </button>
        <button className="btn btn-danger mr-2"> Delete </button>
      </span>
    </li>
  ))
  };


  render(){
    return(
      <main className="content p-3 mb-2 bg-info">
        <h1 className="text-white text-uppercase text-center my-4">Task Manager</h1>
        <div className="row">
          <div className="col-md-6 col-sma-10 mx-auto p-0">
            <dis className="card p-3">
              <div>
                <button className="btn btn-warning">Add Task</button>
              </div>
              {this.renderTabList()}
              <ul className="list-group list-group-flush">
              {this.renderItems()}
              </ul>
            </dis>
          </div>
        </div>
        <footer className="my-3 mb-2 bg-info text-white text-center">Copyright 2023 &copy; All Rights Reserved</footer>
        {this.state.modal ? (
          <Modal activeItem={this.state.activeItem} toggle={this.toggle} onSave={this.handleSubmit}/>
        ) : null} 
      </main>
    )
  }

}


export default App;
